import React, { Component } from 'react';
import axios from 'axios';
import Todo from './Todo'; 

class App extends Component {
  url='http://localhost:5001/'
  state = {
    books: [],
  }
  componentWillMount() {
    axios.get(this.url+"view_books", {headers: {"API_KEY": "H10JZ74AT8CBUY57TP87"}}).then(res => {
      this.setState({
        books: res.data
      })
    });
  }
  render() {
    let books = this.state.books.map((book) => {
      return (
        <tr key={book.book_id}>
          <td>{book.id}</td>
          <td>{book.title}</td>
          <td>{books.author}</td>
          <td>
            <button size="sm" className="mr-2">Edit</button>
            <button size="sm" className="mr-2">Delete</button>
          </td>
        </tr>
      )
    });
    return (
      <div className="App-container">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Author</th>
            </tr>
          </thead>
          <tbody>
            {books}
          </tbody>
        </table>
      </div>

      );
  }
}
export default App;
