import React, { Component } from 'react';
import axios from 'axios';
import { Table, Button } from 'reactstrap';
//import './App.css';

class App extends Component {
  state = {
    books: []
  }
  componentWillMount() {
    axios.get('http://localhost:5001/view_books').then((response)=> {

      console.log(response.data);
      this.setState(
        {
          books: response.data
        }
      )
    })
  }
  render() {
    let books = this.state.books.map((book) => {
      return (
        <tr key={book.book_id}>

          <td>{book.book_id}</td>
          <td>{book.author}</td>
          <td>{book.description}</td>
          <td>
            //<Button color="succes" size="sm" className="mr-50">Edit</Button>
            //<Button color="danger" size="sm">Delete</Button>
          </td>
        </tr>
      )

    });
    return (
      <div className="App container">
        <Table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Author</th>
              <th>Actions</th>

            </tr>
          </thead>

          <tbody>

          </tbody>
        </Table>
      </div>
    )
  }
}

export default App;
