import React, { Component } from 'react';
import axios from 'axios';
import Todo from './Todo'; 

class App extends Component {


  myHeaders={"API_KEY": "H10JZ74AT8CBUY57TP87"}
  state = {
    url: 'http://localhost:5001/',
    books: [],
  }
  getData(){
    axios.get(this.state.url+"view_books", {headers: this.myHeaders}).then(res => {
      this.setState({
        books: res.data
      })
    });
  }
  componentDidMount() {
    this.getData();
  }
  onDeleteHandle() {
    //axios.delete(this.url+"book/"+arguments[0],{crossdomain: true}, {headers: {"API_KEY": "H10JZ74AT8CBUY57TP87", "Access-Control-Allow-Methods": "*"}})
    axios.delete(this.state.url+"book/"+arguments[0], {headers: this.myHeaders}).then(res => {
      if(res.status==200) {
        this.getData();
      }
    })
  }
  onEditHandle() {
    axios.patch(this.state.url+"book/"+arguments[0], {headers: this.myHeaders}).then(res => {
      if(res.status=200) {
        this.getData();
      }
    })
  }
  render() {
    console.log(this.state.books)
    let books = this.state.books.map((book) => {
      return (
        <tr key={book.book_id}>
          <td>{book.book_id}</td>
          <td>{book.name}</td>
          <td>{book.author}</td>
          <td>{book.user_id}</td>
          <td>
            <button>Edit</button>
            <button onClick={this.onDeleteHandle.bind(this, book.book_id)}>Delete</button>
          </td>
        </tr>
      )
    });

    return (
      <div className="App-container">
        {this.renderDeleteInfo}
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Author</th>
              <th>Owner ID</th>
            </tr>
          </thead>
          <tbody>
            {books}
          </tbody>
        </table>
      </div>

      );
  }
}
export default App;
