import React, { Component } from 'react';
import axios from 'axios';
import Todo from './Todo'; 

class App extends Component {
  url='http://localhost:5001/'
  apiKeyHeader={"API_KEY": "H10JZ74AT8CBUY57TP87"}
  state = {
    books: [],
  }
  componentDidMount() {
    axios.get(this.url+"view_books", {headers: this.apiKeyHeader}).then(res => {
      this.setState({
        books: res.data
      })
    });
  }
  onDeleteHandle() {
    axios.delete(this.url+"book/"+book.book_id,)
  }
  render() {
    console.log(this.state.books)
    let books = this.state.books.map((book) => {
      return (
        <tr key={book.book_id}>
          <td>{book.book_id}</td>
          <td>{book.name}</td>
          <td>{book.author}</td>
          <td>
            <button>Edit</button>
            <button onClick={this.onDeleteHandle.bind(this, book.book_id)}>Delete</button>
          </td>
        </tr>
      )
    });

    return (
      <div className="App-container">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Author</th>
            </tr>
          </thead>
          <tbody>
            {books}
          </tbody>
        </table>
      </div>

      );
  }
}
export default App;
