import React from 'react';

export default class Form extends React.Component {
  state = {
    title: "",
    author: "",
    description: ""
  }
  change(e) {
    this.props.onChange({
      [e.target.name]: e.target.value
    });
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  onSubmit(e) {
    e.preventDefault();
    this.setState({
      title: "",
      author: "",
      description: ""
    });
    this.props.onChange({
      title: "",
      author: "",
      description: ""
    });
  }
  render() {
    return (
      <form>
        <br />
        <input 
          name='title'
          placeholder='Title'
          value={this.state.title}
          onChange={e => this.change(e)}
        />
        <br />
        <input 
          name='author'
          placeholder='Author'
          value={this.state.author}
          onChange={e => this.change(e)}
        />
        <br />
        <input 
          name='description'
          placeholder='Description'
          value={this.state.description}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Submit</button>
      </form>
    )
  }
}
