import React, { Component } from 'react';
import axios from 'axios';
import Todo from './Todo'; 

class App extends Component {
  url='http://localhost:5001/'
  state = {
    books: [],
    status: false
  }
  constructor(props){
    super(props)
    axios.get(this.url+"view_books", {headers: {"API_KEY": "H10JZ74AT8CBUY57TP87"}}).then(res => {
      this.setState({
        books: res.data
      })
    });
    /*fetch(this.url+"view_books", {
      method: "GET",
      headers: {
        "API_KEY": "H10JZ74AT8CBUY57TP87"
    }}).then((response) => {
      if(!response.ok) {
        throw Error("ni no respoonse")
      }
      this.setState({
        books:response.json()
      })
    });*/
  }
  render() {
    let books = this.state.books.map((book) => {
      return (
        <tr key={book.book_id}>
          <td>{book.id}</td>
          <td>{book.title}</td>
          <td>{books.author}</td>
          <td>
            <Button size="sm" className="mr-2">Edit</Button>
            <Button size="sm" className="mr-2">Delete</Button>
          </td>
        </tr>
      )
    });
    return (
      <div className="App-container">
        <Table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Author</th>
            </tr>
          </thead>
          <tbody>
            {books}
          </tbody>
        </Table>
      </div>

      );
  }
}
export default App;
